WHERE {
?asteroid wdt:P31 wd:Q3863;
wdt:P61 ?discoverer .
}
GROUP BY ?discoverer
ORDER BY DESC(?count)
LIMIT 20
} AS %i
WHERE {
INCLUDE %i
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" . }
}
ORDER BY DESC(?count)

query.608:
