?item wdt:P31 wd:Q5;
p:P569/psv:P569 [
wikibase:timeValue ?dateOfBirth;
wikibase:timePrecision ?precision
].
}
GROUP BY ?dateOfBirth
}
BIND(YEAR(?dateOfBirth) AS ?yearOfBirth)
}
GROUP BY ?yearOfBirth
ORDER BY DESC(?total)
LIMIT 100

