IF((YEAR(?minDate)) < 1990, "1980 - 1989",
IF((YEAR(?minDate)) < 2000, "1990 - 1999",
IF((YEAR(?minDate)) < 2010, "2000 - 2009",
"2010+")))))
AS ?dateBin)
{
SELECT ?item (MIN(?date) AS ?minDate) ?coord ?image WHERE {
?item wdt:P361 wd:Q735559.
?item wdt:P625 ?coord.
OPTIONAL { ?item wdt:P18 ?image. }
