query.1242:
SELECT ?countryLabel ?weekday ?count ?totalCount ?percentage
WITH {
SELECT ?country ?offset (COUNT(DISTINCT ?election) AS ?count) WHERE {
?election wdt:P31/wdt:P279* wd:Q40231;
wdt:P17 ?country;
p:P585/psv:P585 [
wikibase:timeValue ?date;
wikibase:timePrecision ?precision
].
FILTER(?precision >= "11"^^xsd:integer)
MINUS { ?country wdt:P156|wdt:P576|wdt:P582 ?end. }
BIND(?date - "1776-07-04"^^xsd:dateTime AS ?offsetInt)
BIND(?offsetInt - (7 * xsd:integer(xsd:float(?offsetInt) / 7.0)) AS ?offsetSigned)
BIND(IF(?offsetSigned < 0, ?offsetSigned + 7, ?offsetSigned) AS ?offset)
}
GROUP BY ?country ?offset
} AS %offsets
WITH {
SELECT ?country ?weekday ?count WHERE {
INCLUDE %offsets.
BIND(IF(?offset = 0, "Thursday"@en,
IF(?offset = 1, "Friday"@en,
IF(?offset = 2, "Saturday"@en,
IF(?offset = 3, "Sunday"@en,
IF(?offset = 4, "Monday"@en,
IF(?offset = 5, "Tuesday"@en,
IF(?offset = 6, "Wednesday"@en,
"unknown day"@en)))))))
AS ?weekday)
}
} AS %weekdays
WITH {
SELECT ?country (MAX(?count) AS ?maxCount) (SUM(?count) AS ?totalCount) WHERE {
INCLUDE %weekdays.
}
GROUP BY ?country
HAVING(?totalCount >= 3) # filter out countries with not enough data (3 is a very generous limit)
} AS %maxWeekdayCount
WITH {
SELECT ?country (SAMPLE(?weekday) AS ?weekday) ?count ?totalCount WHERE {
INCLUDE %weekdays.
INCLUDE %maxWeekdayCount.
FILTER(?count = ?maxCount)
}
GROUP BY ?country ?count ?totalCount
} AS %maxWeekday
WHERE {
INCLUDE %maxWeekday.
BIND(?count / ?totalCount AS ?ratio)
BIND(CONCAT(SUBSTR(STR(100 * ?ratio), 1, 5), "%") AS ?percentage)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?totalCount)

query.1243:
SELECT DISTINCT
?item
(SAMPLE(?item_label) AS ?battle)
(SAMPLE(?place_label) AS ?place_label)
(SAMPLE(?partOf_label) AS ?partOf_label)
(SAMPLE(?year) AS ?year)
(SAMPLE(?image) AS ?image)
(SAMPLE(?layer) AS ?layer)
(SAMPLE(?coords) AS ?coords)
(SAMPLE(?rgb) AS ?rgb)
WHERE {
{
?item wdt:P31/wdt:P279* wd:Q178561 .
} UNION {
?item wdt:P31/wdt:P279* wd:Q188055 .
}
OPTIONAL {
?item rdfs:label ?item_label .
FILTER(LANG(?item_label) IN ('fr', 'en')) .
}
OPTIONAL {
?item wdt:P361 ?partOf .
?partOf rdfs:label ?partOf_label .
FILTER(LANG(?partOf_label) IN ('fr', 'en')) .
}
OPTIONAL { ?item wdt:P18 ?image . }
# Place
?item wdt:P276 ?place .
OPTIONAL {
?place rdfs:label ?place_label .
FILTER(LANG(?place_label) IN ('fr', 'en')) .
}
# Coords
OPTIONAL { ?item wdt:P625 ?coordsItem . }
OPTIONAL { ?place wdt:P625 ?coordsPlace . }
BIND(COALESCE(?coordsItem, ?coordsPlace) AS ?coords)
# Date
?item (wdt:P585|wdt:P580|wdt:P582) ?date .
BIND(YEAR(?date) AS ?year) .
# Layer
VALUES (?layer ?min ?max ?rgb) {
("Prehistory" -100000 -3500 "0078BF")
("Ancien History -3500 - 500" -3500 500 "DDD700")
("Middle Ages 500 - 1500" -500 1500 "5BD500")
("Early Modern Period 1500 - 1750" 1500 1750 "00CE23")
("Mid Modern Period 1750 - 1914" 1750 1914 "00C699")
("Contemporary Period 1914 - present" 1914 3500 "0078BF")
}
FILTER (?min <= ?year && ?year < ?max)
} GROUP BY ?item ORDER BY ?year ?battle

