query.1307:
SELECT ?occupationLabel ?women ?men WITH {
SELECT ?occupation (SUM(?woman) AS ?women) (SUM(?man) AS ?men) WHERE {
BIND(wd:Q30 AS ?country). # substitute country of your choice here (remove the Q30, hit Ctrl+Space and start typing the country name, select the right one from the suggestions with the arrow keys and insert it with Enter)
?person wdt:P31 wd:Q5;
wdt:P27 ?country; # remove this to search world-wide - results in query timeout unless you reduce the dataset in some other fashion
p:P106/ps:P106 ?occupation.
{ ?person wdt:P21 wd:Q6581097. BIND(1 AS ?man). } UNION
{ ?person wdt:P21 wd:Q2449503. BIND(1 AS ?man). } UNION
{ ?person wdt:P21 wd:Q6581072. BIND(1 AS ?woman). } UNION
{ ?person wdt:P21 wd:Q1052281. BIND(1 AS ?woman). }
# TODO consider how to count P21: Q48270 (genderqueer) and Q1097630 (intersex)
}
GROUP BY ?occupation
} AS %results WHERE {
INCLUDE %results. # subquery as optimization: don't make the label service fetch labels for every ?person in the subquery
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}
ORDER BY
# divide ?variable+1 instead of ?variable to avoid division by zero
# (unfortunately, we can't use ABS(LOG(x/y)) instead of the IF() because SPARQL has no LOG function)
DESC(IF(?women > ?men, (?women+1)/(?men+1), (?men+1)/(?women+1)))
# if the ratio is the same, show results with more people in total first
DESC(?women+?men)

query.1308:
SELECT ?award (SAMPLE(?label) AS ?awardLabel) ?women ?men WITH {
SELECT ?award (SUM(?woman) AS ?women) (SUM(?man) AS ?men) WHERE {
?person wdt:P31 wd:Q5;
p:P166/ps:P166 ?award.
{ ?person wdt:P21 wd:Q6581097. BIND(1 AS ?man). } UNION
{ ?person wdt:P21 wd:Q2449503. BIND(1 AS ?man). } UNION
{ ?person wdt:P21 wd:Q6581072. BIND(1 AS ?woman). } UNION
{ ?person wdt:P21 wd:Q1052281. BIND(1 AS ?woman). }
}
GROUP BY ?award
HAVING(
# more women than men
?women > ?men &&
# but exclude women-only awards
?men > 0
)
} AS %results WHERE {
INCLUDE %results.
OPTIONAL { ?award wdt:P1448 ?officialName. }
OPTIONAL { ?award rdfs:label ?enLabel. FILTER(LANG(?enLabel) = "en"). }
OPTIONAL { ?award rdfs:label ?anyLabel. }
BIND(COALESCE(?officialName, ?enLabel, ?anyLabel) AS ?label).
}
GROUP BY ?award ?women ?men
ORDER BY STR(?awardLabel)

