?film1 wdt:P31 wd:Q11424;
wdt:P161 ?actor;
wdt:P1476 ?title.
# ditto for ?film2 - since we're using the same ?actor and ?title variable, we require that both films have the same actor and title
?film2 wdt:P31 wd:Q11424;
wdt:P161 ?actor;
wdt:P1476 ?title.
# check that they're not the same film
FILTER(?film1 != ?film2)
}
