query.1594:
SELECT ?date (COUNT(DISTINCT ?item) AS ?count)
WITH {
# get all the dates where the number of living people who have walked on the moon changed
# (person walked on the moon or person died)
SELECT DISTINCT ?date WHERE {
{
# person walked on the moon
?item p:P793 [
ps:P793 wd:Q42882411;
pq:P580 ?date_
].
} UNION {
# person died
?item p:P793/ps:P793 wd:Q42882411;
wdt:P570 ?date_.
}
# for each date, inject a second date, one day before,
# so that we get a straight line until that date and then a steep climb/drop for the real change,
# instead of a diagonal slope over the entire preceding period
VALUES ?toggle { true false }
BIND(IF(?toggle, ?date_, ?date_ - "P1D"^^xsd:duration) AS ?date)
}
} AS %dates
WHERE {
# inject the current time as another date, so that the chart continues until the present day
{ INCLUDE %dates. } UNION { BIND(NOW() AS ?date) }
# main query: find all living people who have walked on the moon at each date
?item p:P793 [
ps:P793 wd:Q42882411;
pq:P580 ?walkdate
].
FILTER(?date >= ?walkdate) # must have walked on the moon already
OPTIONAL { ?item wdt:P570 ?died. }
FILTER(!BOUND(?died) || ?date < ?died) # must not yet have died
}
GROUP BY ?date
ORDER BY ?date

query.1595:
SELECT
?person # find persons ...
?personLabel # ... their label
?secondsInSpace # ... seconds spent in space
(GROUP_CONCAT(DISTINCT ?countryLabel; # ... and all operating countries
SEPARATOR=", ") AS ?countries)
WITH {
# this subquery finds all humans on spaceflights and their operators
SELECT ?person ?flight ?country WHERE {
?flight wdt:P31 wd:Q752783 ; # human spaceflight ...
wdt:P620 [] ; # ... has landed
wdt:P619 [] ; # ... has launched
wdt:P137/wdt:P17 ?country ; # ... sovereign state of operator
wdt:P1029 ?person . # ... with ?person as crew member
}
} AS %spaceflight WITH {
# this subquery finds two distinct launch operators for humans on
# spaceflights. we order countries A and B by their URI, so that we
# can more easily filter out duplicates
SELECT DISTINCT ?person ?countryA ?countryB WHERE {
{ SELECT ?person ?flightA ?countryA WHERE { # find a spaceflight for ?person
INCLUDE %spaceflight .
BIND(?flight AS ?flightA)
BIND(?country AS ?countryA)
}}
{ SELECT ?person ?flightB ?countryB WHERE { # find a second spaceflight for the ?person
INCLUDE %spaceflight .
BIND(?flight AS ?flightB)
BIND(?country AS ?countryB)
}}
FILTER (STR(?flightA) < STR(?flightB)) # enforce that the launches are distinct
FILTER (STR(?countryA) < STR(?countryB)) # enforce that the operating countries are distinct
}} AS %flightCountries
WHERE {
{ SELECT ?person ?country WHERE { # now get both results into the same column
{ SELECT ?person ?country WHERE { # find ?countryA for ?person
INCLUDE %flightCountries .
BIND(?countryA AS ?country)
}} UNION { SELECT ?person ?country WHERE { # find ?countryB for ?person
INCLUDE %flightCountries .
BIND(?countryB AS ?country)
}}
}}
?person wdt:P2873 ?secondsInSpace . # also get the time spent in space
SERVICE wikibase:label { # finally, get the labels ...
bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" .
?person rdfs:label ?personLabel . # ... for person
?country rdfs:label ?countryLabel . # ... and for the country
}
} GROUP BY ?person ?secondsInSpace ?personLabel # group by ?person ...
ORDER BY DESC(?secondsInSpace) ASC(?personLabel) # ... and order by time in space and name

