?d wdt:P31 wd:Q1149652; p:P6499 ?litpopstmt ; p:P6498 ?illitpopstmt ; wdt:P3896 ?shape.
?litpopstmt ps:P6499 ?litpop ; pq:P585 "+2011-01-01T00:00:00Z"^^xsd:dateTime.
?illitpopstmt ps:P6498 ?illitpop ; pq:P585 "+2011-01-01T00:00:00Z"^^xsd:dateTime.
