query.1767:
SELECT ?coauthor_countryLabel (COUNT(?author1) AS ?numberOfCoAuthors)
WITH {
SELECT (COUNT(?work) AS ?count) ?author1 ?author2 WHERE {
# Find co-authors
?searched_author wdt:P496 "0000-0002-6778-0887".
?work wdt:P50 ?searched_author, ?author1, ?author2 .
}
GROUP BY ?author1 ?author2
ORDER BY DESC(?count)
# Limit the size of the graph, to avoid overburdening the browser
LIMIT 1000
} AS %authors
WITH {
SELECT ?author1 ?author2 ?coauthor_country WHERE {
INCLUDE %authors
# Exclude self-links
FILTER (?author1 != ?author2)
?author1 wdt:P27 ?coauthor_country.
}
} AS %result
WHERE {
INCLUDE %result
# Label the results
SERVICE wikibase:label { bd:serviceParam wikibase:language "en,fr,de,ru,es,zh,jp". }
}GROUP BY ?coauthor_countryLabel
ORDER BY DESC(?numberOfCoAuthors)

query.1770:
SELECT DISTINCT ?personDBP ?personWD ?personWDLabel ?BirthDateDBP ?BirthDateWD
WHERE {
SERVICE <http://dbpedia.org/sparql> {
?personDBP a dbo:Philosopher ;
dbo:birthDate ?BirthDate_DBP;
owl:sameAs ?personWD .
FILTER regex (?personWD, "wikidata.org")
BIND (xsd:date(?BirthDate_DBP) AS ?BirthDateDBP)
}
SERVICE <https://query.wikidata.org/sparql> {
?personWD wdt:P569 ?BirthDate_WD .
BIND (xsd:date(?BirthDate_WD) AS ?BirthDateWD)
}
FILTER (?BirthDateDBP != ?BirthDateWD)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?BirthDateDBP)
LIMIT 50

query.1771:
SELECT ?geo ?causes ?countryLabel ?rgb ?population (CONCAT(str(year(?datum)),"-",CONCAT(IF(month(?datum)<10,"0",""),str(month(?datum))),"-",CONCAT(IF(day(?datum)<10,"0",""),str(day(?datum)))) AS ?when) {
?item wdt:P1269 wd:Q81068910 .
?item wdt:P17 ?country .
?country wdt:P1082 ?pop.
?country wdt:P3896 ?geo .
MINUS { ?item wdt:P31 wd:Q186117 . }
OPTIONAL { ?item p:P1603 ?p1603stm .
?p1603stm ps:P1603 ?nr_causes .
?p1603stm wikibase:rank ?rank . FILTER(?rank=wikibase:PreferredRank)
OPTIONAL{?p1603stm pq:P585 ?datum }
}
BIND(IF(?nr_causes>=100,'FF0000',IF(?nr_causes>=10,'FF3333',IF(?nr_causes>=5,'FF6666',IF(?nr_causes>=2,'FF9999',IF(?nr_causes>0,'FFCCCC','FFFFFF'))))) AS ?rgb)
BIND(CONCAT("Number of cases coronavirus reported: ", str(?nr_causes)) AS ?causes)
BIND((round(?pop/100000)/10) AS ?popM )
BIND(CONCAT("Population (million): ", str(round(?popM))) AS ?population)
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

