query.1784:
SELECT ?item ?itemLabel ?code ?native_label (lang(?native_label) AS ?lang) ?missing1 ?missing2
WITH {
SELECT ?item ?native_label ?code WHERE {
?item wdt:P463 wd:Q8908 .
?item wdt:P1705 ?native_label .
?item wdt:P297 ?code .
}
} AS %countries
WHERE {
INCLUDE %countries .
OPTIONAL {
INCLUDE %countries .
BIND(SUBSTR(?code,1,1) AS ?first) .
BIND(?first AS ?missing1) .
FILTER(! CONTAINS(UCASE(?native_label), ?first) )
}
OPTIONAL {
INCLUDE %countries .
BIND(SUBSTR(?code,2,1) AS ?second) .
BIND(?second AS ?missing2) .
FILTER(! CONTAINS(UCASE(?native_label), ?second) ) .
}
FILTER(bound(?missing1) || bound(?missing2)) .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY ?itemLabel

