WITH {
SELECT ?item (MAX(?end_time) AS ?latest_mp) (MAX(?dod) AS ?dod1) (MAX(?dob) AS ?dob1) WHERE {
OPTIONAL {?item wdt:P570 ?dod} .
?item p:P39 ?mp_stmt .
?mp_stmt ps:P39 ?mp .
?mp wdt:P279 wd:Q16707842 .
?mp_stmt pq:P582 ?end_time .
FILTER(year(?end_time) > 1899) .
OPTIONAL {?item wdt:P569 ?dob}
} GROUP BY ?item
} AS %mps
WHERE {
INCLUDE %mps .
?item p:P39 ?mp_stmt .
?mp_stmt pq:P582 ?latest_mp .
?mp_stmt pq:P1534 ?end_cause .
BIND(COALESCE(?dod1, now()) AS ?date) .
BIND (((?date - ?latest_mp) / 365.24) AS ?time) .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY DESC(?time)
LIMIT 200

