query.1969:
SELECT ?item ?itemLabel ?depth1 ?depth2 ?depth WITH{
SELECT ?item ?depth1 ?depth2 WHERE
{
{
SERVICE gas:service
{
gas:program gas:gasClass "com.bigdata.rdf.graph.analytics.SSSP" ; gas:in wd:Q23 ; gas:traversalDirection "Reverse" ; gas:out ?item ;gas:out1 ?depth1 ; gas:maxIterations 1000 ; gas:linkType wdt:P40 .
}
}
{
SERVICE gas:service
{
gas:program gas:gasClass "com.bigdata.rdf.graph.analytics.SSSP" ; gas:in wd:Q207 ; gas:traversalDirection "Reverse" ; gas:out ?item ;gas:out1 ?depth2 ; gas:maxIterations 1000 ; gas:linkType wdt:P40 .
}
}
} } AS %1 WITH {SELECT (?item2 AS ?item1) {INCLUDE %1. ?item2 wdt:P40 ?item.}} AS %2
WHERE {
INCLUDE %1.
FILTER NOT EXISTS {SELECT (?item1 AS ?item) {INCLUDE %2.}}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
BIND(?depth1+?depth2 AS ?depth)
} ORDER BY ?depth

