query.2075:
SELECT DISTINCT ?item ?itemLabel ?img ?georef ?proportion ?coords ?line ?rgb ?layer
WITH {
SELECT ?item ?img ?georef ?coords ?lat ?lon WHERE {
?item wdt:P195 wd:Q7100888 .
?item p:P5140 ?coord_stmt .
OPTIONAL {?item wdt:P9361 ?georef} .
OPTIONAL {?item wdt:P18 ?img} .
?coord_stmt ps:P5140 ?coords .
?coord_stmt psv:P5140 / wikibase:geoLatitude ?lat .
?coord_stmt psv:P5140 / wikibase:geoLongitude ?lon .
}
} AS %points
WITH {
SELECT ?line ?layer ?rgb WHERE {
INCLUDE %points .
?item wdt:P47 ?item2 .
?item2 p:P5140 ?coord2_stmt .
?coord2_stmt psv:P5140 / wikibase:geoLatitude ?lat2 .
?coord2_stmt psv:P5140 / wikibase:geoLongitude ?lon2 .
BIND(CONCAT('LINESTRING (', STR(?lon), ' ', STR(?lat), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line) .
BIND('adjacent' AS ?layer) .
BIND('FF0000' AS ?rgb) .
}
} AS %lines
WITH {
SELECT ?line ?layer ?rgb ?proportion WHERE {
INCLUDE %points .
?item p:P3179 ?stmt .
?stmt ps:P3179 ?item2 .
OPTIONAL {?stmt pq:P1107 ?prop1} .
OPTIONAL {?item2 p:P3179 ?stmt2 . ?stmt2 ps:P3179 ?item . ?stmt2 pq:P1107 ?prop2}
BIND(IF(bound(?prop2) && bound(?prop1) && (?prop2 > ?prop1), ?prop2, ?prop1) AS ?prop) .
BIND(COALESCE(?prop, ?prop1, ?prop2) AS ?proportion) .
?item2 p:P5140 ?coord2_stmt .
?coord2_stmt psv:P5140 / wikibase:geoLatitude ?lat2 .
?coord2_stmt psv:P5140 / wikibase:geoLongitude ?lon2 .
BIND(CONCAT('LINESTRING (', STR(?lon), ' ', STR(?lat), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line) .
BIND('overlap' AS ?layer)
BIND('0000AA' AS ?rgb) .
}
} AS %lines2
WHERE {
{ INCLUDE %lines }
UNION
{ INCLUDE %lines2 }
UNION
{ INCLUDE %points . BIND('points' AS ?layer) . BIND('FF0000' AS ?rgb)} .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

query.2076:
SELECT ?str ?bathwater ?bathwaterLabel ?bathcoord ?lake ?lakeLabel ?lakecoord ?dist ?line ?SJOID ?layer ?EionetBathingWaterIDentifier ?Hav ?VISS WHERE {
?bathwater wdt:P6104 wd:Q106774536.
?bathwater wdt:P625 ?xx.
?bathwater p:P625 [ a wikibase:BestRank ;
psv:P625 [
wikibase:geoLatitude ?bathcoordlat ;
wikibase:geoLongitude ?bathcoordlon ;
wikibase:geoGlobe ?globe ;
] ;
ps:P625 ?bathcoord
]
OPTIONAL { ?bathwater wdt:P9616 ?EionetBathingWaterIDentifier. }
{
?bathwater wdt:P206 ?lake.
?lake wdt:P625 ?rr.
?lake p:P625 [a wikibase:BestRank ;
psv:P625 [
wikibase:geoLatitude ?lat ;
wikibase:geoLongitude ?long ;
wikibase:geoGlobe ?globe2 ;
] ;
ps:P625 ?lakecoord
]
OPTIONAL { ?lake wdt:P761 ?SJOID. }
}
BIND(geof:distance(?bathcoord, ?lakecoord) as ?dist) .
#filter (?dist >= 3)
BIND (floor(?dist/10)*10 as ?layer)
BIND(CONCAT('LINESTRING (', STR(?bathcoordlon), ' ', STR(?bathcoordlat), ',', STR(?long), ' ', STR(?lat), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line)
BIND(URI(CONCAT("https://badplatsen.havochvatten.se/badplatsen/karta/#/bath/",?EionetBathingWaterIDentifier)) AS ?Hav)
BIND(URI(CONCAT("https://viss.lansstyrelsen.se/Waters.aspx?waterEUID=SE",?SJOID)) AS ?VISS)
SERVICE wikibase:label { bd:serviceParam wikibase:language "en,sv". }
}
