query.2080:
SELECT ?x ?xLabel ?loc ?layer ?img ?line ?birthplace ?birthplaceLabel ?teamLabel WITH {
SELECT ?team ?inception WHERE {
BIND ( wd:Q9617 AS ?team )
?team wdt:P571 ?inception .
}} AS %team
WITH { SELECT ?num ?start ?end ?gap WHERE {
INCLUDE %team
BIND (10 * FLOOR(YEAR(?inception) / 10) AS ?start)
BIND (10 AS ?gap )
BIND (10 * FLOOR(YEAR(NOW()) / 10) AS ?end )
BIND ( (?end - ?start ) / ?gap + 1 AS ?x )
[] p:P31 [
ps:P31 wd:Q21199 ;
pq:P155 ?prev].
?prev wdt:P1181 ?num .
FILTER (?num > 0 && ?num <= ?x )
} } AS %range
WITH { SELECT ?decade WHERE {
INCLUDE %range
BIND (?start + (?num - 1) * ?gap AS ?decade)
} ORDER BY ?decade }
AS %decades
WITH { SELECT ?x ?start_year ?end_year ?team WHERE {
INCLUDE %team
BIND ( NOW() AS ?today )
?x wdt:P31 wd:Q5.
?x p:P54 ?stmt.
?stmt ps:P54 ?team.
?stmt pq:P580 ?start.
OPTIONAL { ?stmt pq:P582 ?end }
BIND ( IF (wikibase:isSomeValue(?end), ?today, ?end ) AS ?new_end)
BIND(COALESCE(?new_end, ?today) as ?new_end)
BIND (YEAR(?start) AS ?start_year)
BIND (YEAR(?new_end) AS ?end_year)
} } AS %players
WHERE {
INCLUDE %players
INCLUDE %decades
?x wdt:P19 ?birthplace.
?birthplace wdt:P625 ?loc.
OPTIONAL { ?x wdt:P18 ?img }
FILTER ( (?start_year >= ?decade && ?start_year < ?decade + 10) || (?end_year < ?decade + 10 && ?end_year >= ?decade ) || (?decade > ?start_year && ?decade < ?end_year ))
?birthplace p:P625 [
ps:P625 [];
psv:P625 [
wikibase:geoLongitude ?poblon;
wikibase:geoLatitude ?poblat;
]] .
?team wdt:P115 ?home .
?home p:P625 [
ps:P625 [];
psv:P625 [
wikibase:geoLongitude ?centrelon;
wikibase:geoLatitude ?centrelat]] .
BIND(CONCAT('LINESTRING (', STR(?poblon), ' ', STR(?poblat), ',', STR(?centrelon), ' ', STR(?centrelat), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line)
BIND(CONCAT(STR(?decade),"s") AS ?layer)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ASC(?layer)

query.2082:
SELECT ?item ?itemLabel ?desc ?coords WITH {
SELECT ?item ?lon ?lat ?coords WHERE {
?item wdt:P31 wd:Q515 .
?item wdt:P17 wd:Q145 .
?item p:P625 [
ps:P625 ?coords;
psv:P625 [
wikibase:geoLongitude ?lon;
wikibase:geoLatitude ?lat;
]
] .
FILTER (?lat > 40) # filtering out British Oversea Terrroties
} } AS %cities
WITH { SELECT ?item ?desc ?coords {
INCLUDE %cities
BIND ("Most easterly" AS ?desc)
} ORDER BY DESC (?lon)
LIMIT 1 } AS %easterly
WITH { SELECT ?item ?desc ?coords {
INCLUDE %cities
BIND ("Most westerly" AS ?desc)
} ORDER BY ASC (?lon)
LIMIT 1 } AS %westerly
WITH { SELECT ?item ?desc ?coords {
INCLUDE %cities
BIND ("Most northerly" AS ?desc)
} ORDER BY DESC (?lat)
LIMIT 1 } AS %northerly
WITH { SELECT ?item ?desc ?coords {
INCLUDE %cities
BIND ("Most southernly" AS ?desc)
} ORDER BY ASC (?lat)
LIMIT 1 } AS %southerly
WHERE {
{ INCLUDE %easterly }
UNION
{ INCLUDE %westerly }
UNION
{ INCLUDE %northerly }
UNION
{ INCLUDE %southerly }
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

query.2083:
SELECT DISTINCT ?item ?itemLabel ?height ?countryLabel ?subjectLabel ?coords ?image WITH {
SELECT ?item ?heightStatement WHERE {
?item wdt:P31 ?type .
VALUES ?type { wd:Q1779653 wd:Q179700 wd:Q1051606 wd:Q29168169 wd:Q3476533 }
FILTER NOT EXISTS { ?item wdt:P31 wd:Q21745157 }
FILTER NOT EXISTS { ?item wdt:P5816 wd:Q56556915 }
?item p:P2048 ?heightStatement .
{ ?heightStatement pq:P518 wd:Q179700 } UNION
{ FILTER NOT EXISTS { ?heightStatement pq:P518 ?x } }
}
} AS %i WHERE {
INCLUDE %i .
?heightStatement psv:P2048 [
wikibase:quantityAmount ?baseHeight;
wikibase:quantityUnit [
p:P2370/psv:P2370 [
wikibase:quantityAmount ?unitHeight ;
wikibase:quantityUnit wd:Q11573
]
]
].
BIND ( ?baseHeight * ?unitHeight AS ?height )
FILTER ( ?height >= 30 )
OPTIONAL { ?item wdt:P17 ?country }
OPTIONAL { ?item wdt:P625 ?coords }
OPTIONAL { ?item wdt:P18 ?image }
OPTIONAL { ?item wdt:P921 ?subject }
#SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" }
SERVICE wikibase:label { bd:serviceParam wikibase:language "en" }
}
ORDER BY DESC(?height) ?countryLabel ?itemLabel

