query.2176:
SELECT ?misto_narozeni ?misto_umrti ?line WITH {
SELECT ?item ?misto_narozeni ?misto_umrti where {
?item wdt:P19/wdt:P17 wd:Q213 .
?item wdt:P20/wdt:P17 wd:Q213 .
?item wdt:P19* ?misto_narozeni . ?misto_narozeni wdt:P31 wd:Q5153359 .?misto_narozeni wdt:P1082 ?obyvatele filter(?obyvatele > 2500 ) .
?item wdt:P20* ?misto_umrti . ?misto_umrti wdt:P31 wd:Q5153359 filter(?misto_narozeni != ?misto_umrti).
}
} AS %data WITH {
SELECT ?misto_narozeni ?misto_umrti (COUNT(?item) AS ?pocet) {
INCLUDE %data .
} GROUP BY ?misto_narozeni ?misto_umrti
} AS %counts WITH {
SELECT ?misto_narozeni (MAX(?pocet) AS ?nej) {
INCLUDE %counts .
} GROUP BY ?misto_narozeni
} AS %best WHERE {
INCLUDE %counts .
INCLUDE %best .
?misto_narozeni p:P625 [ ps:P625 []; psv:P625 [ wikibase:geoLongitude ?coord1lon; wikibase:geoLatitude ?coord1lat; ] ].
?misto_umrti p:P625 [ ps:P625 []; psv:P625 [ wikibase:geoLongitude ?coord2lon; wikibase:geoLatitude ?coord2lat; ] ].
BIND(CONCAT('LINESTRING (', STR(?coord1lon), ' ', STR(?coord1lat), ',', STR(?coord2lon), ' ', STR(?coord2lat), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line)
FILTER( ?nej = ?pocet ) .
} order by asc(?misto_narozeni)

