query.2225:
SELECT ?occ ?occLabel ?avgAge ?avgBirthYear ?count
WHERE
{
{
# Group the people by their occupation and calculate age
SELECT
?occ
(count(?p) as ?count)
(round(avg(?birthYear)) as ?avgBirthYear)
(avg(?deathYear - ?birthYear) as ?avgAge)
WHERE {
{
# Get people with occupation + birth/death dates; combine multiple birth/death dates using avg
SELECT
?p
?occ
(avg(year(?birth)) as ?birthYear)
(avg(year(?death)) as ?deathYear)
WHERE {
?p wdt:P31 wd:Q5 ;
wdt:P106 ?occ ;
p:P569/psv:P569 [
wikibase:timePrecision "9"^^xsd:integer ; # precision of at least year
wikibase:timeValue ?birth ;
] ;
p:P570/psv:P570 [
wikibase:timePrecision "9"^^xsd:integer ; # precision of at least year
wikibase:timeValue ?death ;
] .
}
GROUP BY ?p ?occ
}
}
GROUP BY ?occ
LIMIT 200
}
FILTER (?count > 300) # arbitrary number to weed out values with 'few' observations
SERVICE wikibase:label { bd:serviceParam wikibase:language "fr" . }
}
ORDER BY ASC(?avgAge)

query.2226:
SELECT ?item ?itemLabel ?itemDescription ?somePositionLabel ?died ?kind WITH {
SELECT ?item (MAX(?died_) AS ?died) (SAMPLE(?kind_) AS ?kind) (SAMPLE(?position) AS ?somePosition) {
hint:SubQuery hint:optimizer "None".
# death in battle or killed in action
{ ?item wdt:P1196 wd:Q18663901. BIND("death in battle"@en AS ?kind_) } UNION
{ ?item wdt:P509|wdt:P1347 wd:Q210392. BIND("killed in action"@en AS ?kind_) }
# suitable item
?item wdt:P31 wd:Q5;
wdt:P570 ?died_.
# head of state or government
{
VALUES ?class { wd:Q2285706 wd:Q48352 }
?item wdt:P39 ?position.
?position wdt:P279* ?class.
} UNION {
?state wdt:P6 ?item.
OPTIONAL { ?state wdt:P1313 ?position_. }
BIND(COALESCE(?position_, wd:Q2285706) AS ?position)
} UNION {
?state wdt:P35 ?item.
OPTIONAL { ?state wdt:P1906 ?position_. }
BIND(COALESCE(?position_, wd:Q48352) AS ?position)
}
}
GROUP BY ?item ?died_
} AS %results WHERE {
INCLUDE %results.
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?died)
LIMIT 100

