query.2230:
select *
with {
select ?lang (count(*) as ?meaningless) {
?l dct:language ?lang;
wikibase:statements "0"^^xsd:integer.
minus { ?l ontolex:sense [] }
} group by ?lang
} as %meaningless
with {
select ?lang (count(*) as ?total) {
?l dct:language ?lang.
} group by ?lang
} as %totals
where {
include %meaningless.
include %totals.
filter (?total >= 10).
bind(?meaningless/?total*100 as ?pc).
?lang rdfs:label ?en filter(lang(?en) = "en").
} order by desc(?pc) desc(?total) ?en

