query.2304:
SELECT ?date ?winner ?winnerLabel ?pm ?pmLabel ?pm_image WITH {
SELECT DISTINCT ?winner ?d WHERE { # all the winners of the English Premier League or First Division
VALUES $ENGLISH_FIRST { wd:Q754839 wd:Q9448 wd:Q213347 }
?x wdt:P3450 $ENGLISH_FIRST .
?x wdt:P1346 ?winner .
?x wdt:P582 ?d
MINUS { ?x wdt:P2499 [] } # make sure this is really the Top league
}} AS %winners
WITH {
SELECT (MAX(?d) AS ?date) ?winner WHERE { # get the latest (MAX) date
INCLUDE %winners
} GROUP BY ?winner} AS %winner_and_date
WHERE { # determine who was the Prime minister on a particular date
INCLUDE %winner_and_date
?pm p:P39 ?ph_stmt .
OPTIONAL { ?pm wdt:P18 ?pm_image }
?ph_stmt ps:P39 wd:Q14211 .
?ph_stmt pq:P580 ?pm_start .
OPTIONAL { ?ph_stmt pq:P582 ?pm_end }
BIND(COALESCE(?pm_end, NOW()) AS ?pm_end)
FILTER (?pm_start <= ?date && ?pm_end >= ?date)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?date

query.2305:
SELECT ?item ?itemLabel ?statements ?property ?propertyLabel ?predicateCount WITH {
SELECT ?statements WHERE { # get numbers from 1k to 10k
?nn wdt:P31 wd:Q21199; wdt:P1181 ?statements .
FILTER (?statements >= 1000 && ?statements <= 10000)
} } AS %numbers
WITH { SELECT ?item ?statements WHERE { # get items having that many statements; exclude scholarly articles
INCLUDE %numbers
BIND(xsd:integer(?statements) as ?b)
?item wikibase:statements ?b.
FILTER NOT EXISTS {?item wdt:P31 wd:Q13442814 .}
} } as %items
WITH { SELECT ?item ?statements ?property (COUNT(?predicate) as ?predicateCount) WHERE { # get & count claims for the items
INCLUDE %items
?item ?predicate [].
?property wikibase:claim ?predicate .
} GROUP BY ?item ?statements ?property } as %counts
WITH { SELECT ?item (MAX(?predicateCount) AS ?maxcount) {
INCLUDE %counts
} GROUP BY ?item } AS %maxCounts
WHERE {
# filter for ?predicateCount = ?maxcount to try to identify most-used predicates per item. (It's a bit of a kludge but...)
INCLUDE %counts
INCLUDE %maxCounts
FILTER (?predicateCount = ?maxcount)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} order by desc(?statements)

