query.2308:
SELECT ?LALabel ?official_website WHERE {
#find all instances of a non-metropolitan county, district, unitary authority or metropolitan borough council
{?LA wdt:P31 wd:Q769603.
}
UNION {?LA wdt:P31 wd:Q1187580.
}
UNION {?LA wdt:P31 wd:Q1136601.
}
UNION {?LA wdt:P31 wd:Q19414242.}
#get all things these are instances of & then get end date for that (i.e. LA is current)
OPTIONAL {?LA p:P31 ?type.
?type pq:P582 ?end_date.}
#get website
OPTIONAL { ?LA wdt:P856 ?official_website. }
#deduplicate (assumes LA cannot be isntance of both these things at same time)
OPTIONAL { ?LA wdt:P31 ?instance_of. }
FILTER((((?instance_of = wd:Q1187580) || (?instance_of = wd:Q1136601)) || (?instance_of = wd:Q769603)) || (?instance_of = wd:Q19414242))
#create value ?test_date. If instance statement had an end date use it, if not use today
BIND(IF(BOUND(?end_date),?end_date,NOW()) AS ?test_date).
#only show LAs with test date today or in future (this means we will show local authorities that exist but are planned to cease in the future)
FILTER(?test_date >= NOW()) .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }}
ORDER BY (?LALabel)

