query.2387:
SELECT distinct ?dec ((?count/?decade_total)*100 as ?percent) ?causeLabel ?count
WITH {
select (str(?decade) as ?dec) ?item ?cause where {
?term wdt:P279 wd:Q16707842 . ?item p:P39 ?ps . ?ps ps:P39 ?term .
?ps pq:P580 ?start . ?ps pq:P582 ?end . ?ps pq:P1534 ?cause .
FILTER NOT EXISTS { ?term2 wdt:P279 wd:Q16707842 .
?item p:P39 ?ps2 . ?ps2 ps:P39 ?term2 . ?ps2 pq:P580 ?start2 . filter (?ps != ?ps2)
optional { ?ps2 pq:P582 ?end2 } BIND(COALESCE(?end2, ?start2) AS ?check) . filter (?check >= ?end) }
# filter out any where they were still serving later on
bind(year(?end) as ?year) . filter(?year >= 1870) . filter(?year < 2020) . bind((floor(?year/10)*10) as ?decade) .
}
} AS %members
WHERE {
{ SELECT ?dec (count(distinct ?item) as ?count) ?cause where
{ INCLUDE %members } group by ?dec ?cause }
{ SELECT ?dec (count(distinct ?item) as ?decade_total) where
{ INCLUDE %members } group by ?dec }
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
#defaultView:BarChart

