?spob p:P625/psv:P625/wikibase:geoLatitude ?lat2 ; p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) . BIND(STRDT(?str, geo:wktLiteral) AS ?line) .
OPTIONAL { ?student wdt:P18 ?image .}
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}
}

query.2431:
SELECT DISTINCT ?pm ?pmLabel ?pmImage ?monarch ?monarchLabel ?monarchImage WITH {
# not sure if there is a more straightforward way to access British monarchs (this query runs second)
SELECT DISTINCT ?monarch ?monarch_start ?monarch_end WHERE {
INCLUDE %start_UK .
VALUES $MONARCH { wd:Q110324075 wd:Q9134365 wd:Q111722535 }
?monarch wdt:P31 wd:Q5 .
?monarch p:P39 ?ph .
?ph ps:P39 $MONARCH .
?ph pq:P580 ?monarch_start .
OPTIONAL { ?ph pq:P582 ?monarch_end }
FILTER (?monarch_start >= ?start_UK || ?monarch_end >= ?start_UK)
BIND ( NOW() AS ?today )
BIND(COALESCE(?monarch_end, ?today) as ?monarch_end) # handle missing end dates, hopefully it's just the current monarch
} } AS %monarchs
# Find the start date of the first Prime Minister so we can filter monarchs that reigned after this point (this query runs first)
WITH { SELECT ?start_UK WHERE {
?pm p:P39 ?pmph_stmt .
?pmph_stmt ps:P39 wd:Q14211 .
?pmph_stmt pq:P580 ?start_UK .
}
ORDER BY ?start_UK
LIMIT 1 } AS %start_UK
# this query runs last and uses the select in line 2; it produces the visualisation specified in line 1
WHERE {
INCLUDE %monarchs
?pm wdt:P31 wd:Q5 .
?pm p:P39 ?node .
?node ps:P39 wd:Q14211 .
OPTIONAL { ?node pq:P580 ?pm_start }
OPTIONAL { ?node pq:P582 ?pm_end }
BIND ( NOW() AS ?today )
BIND(COALESCE(?pm_end, ?today) as ?pm_end) # handle missing end dates, hopefully it's just the current president
FILTER( (?monarch_start >= ?pm_start && ?monarch_start <= ?pm_end) || (?monarch_end >= ?pm_start && ?monarch_end <= ?pm_end ) ||
(?pm_start >= ?monarch_start && ?pm_start <= ?monarch_end) || (?pm_end >= ?monarch_start && ?pm_end <= ?monarch_end ))
# get images, use a default image if none found
OPTIONAL { ?monarch wdt:P18 ?monarchImage }
BIND(COALESCE(?monarchImage, <http://commons.wikimedia.org/wiki/Special:FilePath/Image_needed,_please_upload_to_commons.wikimedia.org.jpg>) as ?monarchImage)
OPTIONAL { ?pm wdt:P18 ?pmImage }
BIND(COALESCE(?pmImage, <http://commons.wikimedia.org/wiki/Special:FilePath/Image_needed,_please_upload_to_commons.wikimedia.org.jpg>) as ?pmImaage)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

query.2432:
SELECT DISTINCT ?president ?presidentLabel ?presidentImage ?monarch ?monarchLabel ?monarchImage WITH {
# not sure if there is a more straightforward way to access British monarchs (this query runs second)
SELECT DISTINCT ?monarch ?monarch_start ?monarch_end WHERE {
INCLUDE %start_US .
VALUES $MONARCH { wd:Q9134365 wd:Q111722535 wd:Q110324075 }
?monarch wdt:P31 wd:Q5 .
?monarch p:P39 ?ph .
?ph ps:P39 $MONARCH .
?ph pq:P580 ?monarch_start .
OPTIONAL { ?ph pq:P582 ?monarch_end }
FILTER (?monarch_start >= ?start_US || ?monarch_end >= ?start_US)
BIND ( NOW() AS ?today )
BIND(COALESCE(?monarch_end, ?today) as ?monarch_end) # handle missing end dates, hopefully it's just the current monarch
} } AS %monarchs
# Find the start date of the first US President so we can filter monarchs that reigned after this point (this query runs first)
WITH { SELECT ?start_US WHERE {
?x wdt:P31 wd:Q5 .
?x p:P39 ?ph .
?ph ps:P39 wd:Q11696 .
?ph pq:P1545 ?number .
?ph pq:P580 ?start_US .
FILTER (?number = "1")
} } AS %start_US
# this query runs last and uses the select in line 2; it produces the visualisation specified in line 1
WHERE {
INCLUDE %monarchs
?president wdt:P31 wd:Q5 .
?president p:P39 ?node .
?node ps:P39 wd:Q11696 .
OPTIONAL { ?node pq:P1545 ?number }
OPTIONAL { ?node pq:P580 ?president_start }
OPTIONAL { ?node pq:P582 ?president_end }
BIND ( NOW() AS ?today )
BIND(COALESCE(?president_end, ?today) as ?president_end) # handle missing end dates, hopefully it's just the current president
FILTER( (?monarch_start >= ?president_start && ?monarch_start <= ?president_end) || (?monarch_end >= ?president_start && ?monarch_end <= ?president_end ) ||
(?president_start >= ?monarch_start && ?president_start <= ?monarch_end) || (?president_end >= ?monarch_start && ?president_end <= ?monarch_end ))
# get images, use a default image if none found
OPTIONAL { ?monarch wdt:P18 ?monarchImage }
BIND(COALESCE(?monarchImage, <http://commons.wikimedia.org/wiki/Special:FilePath/Image_needed,_please_upload_to_commons.wikimedia.org.jpg>) as ?monarchImage)
OPTIONAL { ?president wdt:P18 ?presidentImage }
BIND(COALESCE(?presidentImage, <http://commons.wikimedia.org/wiki/Special:FilePath/Image_needed,_please_upload_to_commons.wikimedia.org.jpg>) as ?presidentImage)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

query.2433:
SELECT ?item ?itemLabel ?itemDescription ?location ?rgb ?image WITH {
SELECT $COUNTRY ?north ?east ?south ?west WHERE {
VALUES $COUNTRY { wd:Q212 }
$COUNTRY p:P1332 [psv:P1332 [wikibase:geoLatitude ?north]].
$COUNTRY p:P1334 [psv:P1334 [wikibase:geoLongitude ?east]].
$COUNTRY p:P1333 [psv:P1333 [wikibase:geoLatitude ?south]].
$COUNTRY p:P1335 [psv:P1335 [wikibase:geoLongitude ?west]].
} } AS %data
WHERE {
INCLUDE %data
?item wdt:P17 $COUNTRY .
?item p:P625 [
ps:P625 ?location ;
psv:P625 [
wikibase:geoLongitude ?longitude;
wikibase:geoLatitude ?latitude;
]
].
OPTIONAL { ?item wdt:P18 ?image }
FILTER (?latitude <= ?north && ?latitude >= ?south && ?longitude >= ?west && ?longitude <= ?east)
BIND ( (?north + ?south) / 2 AS ?centre )
BIND ( COALESCE(
IF(?latitude >= ?centre, "0057b7", 1/0),
"ffd700"
) AS ?rgb )
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
LIMIT 10000 # try to increase the limit but might timeout or cause browser to hang when rendering the map

