SELECT ?language (COUNT(*) AS ?lexemes) (SUM(?hasExternalId) AS ?withExternalId) {
INCLUDE %languages
?lexeme dct:language ?language .
BIND(IF(EXISTS {
?property wdt:P31 wd:Q19847637 ; wikibase:directClaim ?wdt .
?lexeme ?wdt [] .
}, 1, 0) AS ?hasExternalId) .
}
GROUP BY ?language
}
GROUP BY ?language ?lexemes ?withExternalId
} AS %stats WHERE {
INCLUDE %stats
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" }
}
ORDER BY DESC(?lexemes)

