SELECT DISTINCT ?item1 ?item1Label ?article1 ?item2 ?item2Label ?article2
WITH { SELECT DISTINCT ?item WHERE {
VALUES ?membership {wd:Q16707842 wd:Q1801564 wd:Q18018860} .
?item wdt:P31 wd:Q5 ; wdt:P39/wdt:P279* ?membership .
} } AS %mps
WITH { SELECT DISTINCT ?item ?familyname ?firstname_form WHERE {
INCLUDE %mps .
?item wdt:P734 ?familyname .
?firstname_form wdt:P1533 ?familyname .
} } AS %mps1
WITH { SELECT DISTINCT ?item ?familyname ?firstname_form ?firstname ?familyname_form WHERE {
INCLUDE %mps1 .
?item wdt:P735 ?firstname .
?firstname wdt:P1533 ?familyname_form .
} } AS %mps2
WITH { SELECT (?item AS ?item1) (?firstname AS ?firstname1) (?familyname AS ?familyname1) WHERE {
INCLUDE %mps2 .
} } AS %mps2_1
WITH { SELECT (?item AS ?item2) (?firstname_form AS ?firstname1) (?familyname_form AS ?familyname1) WHERE {
INCLUDE %mps2 .
} } AS %mps2_inverted
WHERE {
INCLUDE %mps2_1 .
INCLUDE %mps2_inverted .
FILTER (?item1 != ?item2) .
optional { ?article1 schema:about ?item1 ; schema:isPartOf <https://en.wikipedia.org/> }
optional { ?article2 schema:about ?item2 ; schema:isPartOf <https://en.wikipedia.org/> }
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY ?item2Label

