?layer wdt:P131/wdt:P131 wd:Q22 . hint:Prior hint:gearing "forward".
}
} as %j
WHERE {
INCLUDE %j
{?layer (^wdt:P403|^wdt:P201|wdt:P200)*/(^wdt:P177|^wdt:P206)? ?dot. }
?dot wdt:P625|((p:P885|p:P403|p:P200|p:P201)/pq:P625) ?coord .
