VALUES ?univ { wd:Q2303765 wd:Q1654025 wd:Q746079 }
?researcher (wdt:P108|wdt:P463|wdt:P1416) ?univ .
?researcher wdt:P27 ?lieu .
?lieu wdt:P30 ?cont .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
