?prop wikibase:propertyType ?propType .
}
GROUP BY ?propType
ORDER BY DESC(?props)

query.2549:
SELECT DISTINCT ?item ?itemLabel ?namedAfter ?namedAfterLabel WHERE {
?item wdt:P31 wd:Q6256. # list of all countries (Q6256)
{
?item wdt:P138 ?namedAfter. # countries that are named after a person (Q5)
?namedAfter wdt:P31 wd:Q5.
}
UNION
{
?item wdt:P138 ?middleNamedAfter. # some countries are named after things that are named after people
?middleNamedAfter wdt:P138 ?namedAfter.
?namedAfter wdt:P31 wd:Q5.
}
UNION
{
?item wdt:P138 ?middleNamedAfter. # some countries are named after things that are named after things that are named after people
?middleNamedAfter wdt:P138 ?middle2NamedAfter.
?middle2NamedAfter wdt:P138 ?namedAfter.
?namedAfter wdt:P31 wd:Q5.
}
FILTER(NOT EXISTS { ?namedAfter wdt:P411 wd:Q43115. }) # exclude any Saints (people whose canonisation status is "Saint")
FILTER(NOT EXISTS { ?namedAfter wdt:P411 wd:Q3464126. }) # exclude any Saints (people whose canonisation status is "Catholic Saint")
FILTER(NOT EXISTS { ?namedAfter wdt:P31 wd:Q20643955. }) # exclude any biblical figures (people who are an instance of Human Biblical figures)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY (?namedAfterLabel)

query.2550:
