query.2553:
#defaultView:Table
SELECT ?count ?cited_work ?cited_workLabel
WITH {
  SELECT (count(?work) as ?count) ?cited_work WHERE {
    { ?work wdt:P921/wdt:P31*/wdt:P279* wd:Q23498 . }
    union { ?work wdt:P921/wdt:P361+ wd:Q23498 . }
    union { ?work wdt:P921/wdt:P1269+ wd:Q23498 . }
    ?work wdt:P2860 ?cited_work .
    ?cited_work wdt:P577 ?publicationdate .
FILTER (?publicationdate > "1969-12-31T00:00:00"^^xsd:dateTime) . #works published on the subject after a certain date
FILTER (?publicationdate < "1980-01-01T00:00:00"^^xsd:dateTime) . #works published on the subject before a certain date
  }

  GROUP BY ?cited_work
} AS %result
WHERE {
  INCLUDE %result
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,da,de,es,fr,jp,nl,no,ru,sv,zh" . } 
}
ORDER BY DESC(?count)
LIMIT 10

