query.2571:
SELECT DISTINCT ?np ?statements
with {
select distinct ?statements {
[] wikibase:statements ?statements.
}
} as %values
WHERE {
hint:Query hint:optimizer "None".
VALUES ?instances {
# Those are the different groups of "chemicals" we can be interested in
# What I would like would be this query not to timeout without commenting the 4 below.
# (just letting the last very small group to illustrate)
wd:Q11173 # chemical compound
wd:Q59199015 # group of stereoisomers
wd:Q79529 # chemical substance
wd:Q17339814 # group of chemical substances
wd:Q47154513 # structural class of chemical compounds
}
include %values.
filter (?statements > 100).
?np wikibase:statements ?statements;
wdt:P31 ?instances;
(p:P703/ps:P703) ?t. # This filters chemicals found in a taxon as a proxy for "natural product"
}
ORDER BY DESC (?statements)

query.2572:
SELECT ?role (count(distinct ?item) as ?count)
WITH {
SELECT DISTINCT ?item ?property ?property_label WHERE {
?item ?predicate wd:Q380875. hint:Prior hint:runFirst true.
?item wdt:P31/wdt:P279* wd:Q811430 .hint:Prior hint:gearing "forward".
?property wikibase:directClaim ?predicate .
?property rdfs:label ?property_label . FILTER(lang(?property_label) = 'en') .
}
} AS %items
WITH {
SELECT ?item (GROUP_CONCAT(?property_label; separator = ' + ') AS ?role) WHERE {
INCLUDE %items
} GROUP BY ?item
} AS %roles
WHERE {
INCLUDE %roles .
} GROUP BY ?role ORDER BY DESC(?count)

query.2573:
select ?item ?itemLabel ?count ?langExample ?langExampleLabel (sample(?lexeme) as ?lex) ?lemma (sample(?sens) as ?sense) {
{
select ?item (count(distinct ?lang) as ?count) (sample(?lang) as ?langExample) {
?sens wdt:P5137 ?item .
?lexeme ontolex:sense ?sens ;
dct:language ?lang .
} group by ?item having (?count > 2)
}
?lexeme ontolex:sense ?sens ;
dct:language ?langExample ;
wikibase:lemma ?lemma .
?sens wdt:P5137 ?item .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} group by ?item ?itemLabel ?count ?langExample ?lemma ?langExampleLabel order by desc(?count)

