WITH {
SELECT ?child WHERE {
VALUES ?taxon {
wd:Q152 # fishes
wd:Q93315 # cyanobacteria
wd:Q103169 # red algae
wd:Q264543 # chlorohyta
wd:Q18960 # sponges
wd:Q28524 # corals
}
?child (wdt:P171*) ?taxon.
}
}
AS %child
WHERE {
INCLUDE %child
?structure wdt:P233 ?structure_smiles;
p:P703[
ps:P703 ?child;
(prov:wasDerivedFrom/pr:P248) ?reference ].
?reference wdt:P356 ?reference_doi.
