}

query.2578:
SELECT DISTINCT ?item ?itemLabel ?population ?altitude ?populationTimesAltitude WITH {
SELECT ?item ?population ?altitude {
?item p:P31 ?pStatement ;
wdt:P1082 ?population ;
wdt:P2044 ?altitude .
?pStatement ps:P31/wdt:P279* wd:Q484170 .
FILTER NOT EXISTS { ?pStatement pq:P576 ?x }
}
} AS %i
WHERE
{
INCLUDE %i
BIND ( (?population * ?altitude) AS ?populationTimesAltitude)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?populationTimesAltitude)
LIMIT 100

query.2580:
SELECT ?actor ?actorLabel ?characterLabel ?count WITH {
SELECT ?actor ?character (COUNT(DISTINCT ?film) AS ?count) WHERE {
?film wdt:P31/wdt:P279* wd:Q11424;
p:P161 [
ps:P161 ?actor;
pq:P453 ?character
].
FILTER(?actor != ?character)
?character wdt:P31 wd:Q5;
wdt:P39 ?position.
}
GROUP BY ?actor ?character
HAVING(?count > 1)
ORDER BY DESC(?count)
LIMIT 100
} AS %results WHERE {
INCLUDE %results.
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?count)
LIMIT 100

