query.2677:
SELECT ?language ?languageLabel ?lexemes ?withExternalId ?percent WITH {
SELECT ?language (COUNT(*) AS ?count) { ?lexeme dct:language ?language }
GROUP BY ?language
ORDER BY DESC(COUNT(*))
LIMIT 20
} AS %languages WITH {
SELECT ?language ?lexemes ?withExternalId (ROUND(10000 / ?lexemes * ?withExternalId) / 100 AS ?percent) {
SELECT ?language (COUNT(*) AS ?lexemes) (SUM(?hasExternalId) AS ?withExternalId) {
INCLUDE %languages
?lexeme dct:language ?language .
BIND(IF(EXISTS {
?property wikibase:propertyType wikibase:ExternalId ; wikibase:directClaim ?wdt .
?lexeme ?wdt [] .
}, 1, 0) AS ?hasExternalId) .
}
GROUP BY ?language
}
GROUP BY ?language ?lexemes ?withExternalId
} AS %stats WHERE {
INCLUDE %stats
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" }
}
ORDER BY DESC(?lexemes)

