?h wdt:P31 wd:Q47566; # instance of (P31) U.S. presidential election (Q47566)
wdt:P585 ?electionDate; # point in time (P585)
wdt:P991 ?president. # successful candidate (P991)
?president wdt:P569 ?birthday # date of birth (P569)
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
