query.2706:
SELECT ?item1 ?image1 ?item1Label ?item2 ?image2 ?item2Label ?size ?rgb ?edgeLabel
WHERE
{
VALUES ?book { wd:Q112075048 }
{ # Get characters in the We Are Here book
# VALUES ?rgb { "FFBD33" }
# VALUES ?size { 2 }
VALUES ?item1 { wd:Q112075048 }
?item1 wdt:P674 ?item2 .
OPTIONAL { ?item1 wdt:P18 ?image1. }
OPTIONAL { ?item2 wdt:P18 ?image2. }
}
UNION
{ # Occupations
# VALUES ?rgb { "fff033" }
# VALUES ?size { 1 }
?book wdt:P674 ?item1 .
?item1 wdt:P106 ?item2 .
?item2 wikibase:sitelinks ?size
OPTIONAL { ?item1 wdt:P18 ?image1. }
BIND(COALESCE(
IF(?size >= 100, "fff033", 1/0),
IF(?size >= 65, "ddf033", 1/0),
IF(?size >= 25, "ccf033", 1/0),
IF(?distNum >= 1, "ffffff", "ffffff")) AS ?rgb).
}
UNION
{ # Get relationships
?book wdt:P674 ?item1 .
?book wdt:P674 ?item2 .
?item1 wdt:P26 ?item2 .
BIND ("spouse" AS ?edgeLabel )
OPTIONAL { ?item1 wdt:P18 ?image1. }
OPTIONAL { ?item2 wdt:P18 ?image2. }
}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} LIMIT 1000

