query.2716:
SELECT ?item ?itemLabel ?image ?date
WHERE
{
{
# query without labels
SELECT ?item (SAMPLE(?image) as ?image) (MIN(?whatever_date) as ?date)
WHERE
{
# item must be a videogame or any subclass of copyright license
wd:Q7889 ^wdt:P279*/^wdt:P31 ?item.
# item must has a license
?item wdt:P275 ?license.
# the license must be a libre license
?libre_license ^wdt:P279*/^wdt:P31 ?license.
VALUES ?libre_license {
wd:Q97044024 # open source
wd:Q3943414 # free software
wd:Q97662661 # free license
wd:Q196294 # open license
}
OPTIONAL {
?item wdt:P18 ?image.
}
# item must have a date (whatever publication date or inception date is fine)
?item wdt:P577|wdt:P571 ?whatever_date.
}
GROUP BY ?item
}
# apply labels after the group by
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

query.2717:
select ?item ?itemLabel ?string ?content ?timestamp ?user {
?item wdt:P31 wd:Q146. # find cats
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
BIND(STRAFTER(wikibase:decodeUri(str(?item)), "http://www.wikidata.org/entity/") AS ?string)
# need to make the ID something the API can understand
SERVICE wikibase:mwapi {
bd:serviceParam wikibase:endpoint "www.wikidata.org";
wikibase:api "Generator";
wikibase:limit "once";
mwapi:generator "allpages";
mwapi:gapfrom ?string;
mwapi:gapnamespace 0 ; # main namespace
mwapi:gaplimit 1; # only one
mwapi:prop "revisions";
mwapi:rvdir "newer"; # FIRST revision
mwapi:rvprop "timestamp|content|user".
?content wikibase:apiOutput 'revisions/rev/text()' . # text of first
?timestamp wikibase:apiOutput 'revisions/rev/@timestamp' . # timestamp
?user wikibase:apiOutput 'revisions/rev/@user' . # user who created it
}
}

query.2718:
SELECT ?award ?awardLabel (ROUND(?women * 1000/(?men + ?women) )/ 10 AS ?percent) WHERE {
{ SELECT (COUNT(?recipient_male) AS ?men) (COUNT(?recipient_female) AS ?women) ?award WHERE {
?award wdt:P31 wd:Q378427 .
{
?recipient_male wdt:P166 ?award .
?recipient_male wdt:P21 wd:Q6581097 .
}
UNION
{
?recipient_female wdt:P166 ?award .
?recipient_female wdt:P21 wd:Q6581072 .
}
}
GROUP BY ?award HAVING(?men + ?women > 50)
}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY ?percent

