query.2727:
SELECT ?employerLabel ?logoImage ?NoVBIBFix (COUNT(?vbib) AS ?NoVBIB) WITH {
SELECT DISTINCT ?employer ?vbib ?logoImage WHERE {
?vbib wdt:P179 wd:Q108784175.
?presentation wdt:P5072 ?vbib;
wdt:P585 ?pit.
BIND(YEAR(?pit) AS ?year)
?presentation wdt:P50 ?author.
?author p:P108 ?employerStmt.
?employerStmt ps:P108 ?employer.
OPTIONAL { ?employerStmt pq:P580 ?empStart.
}
FILTER (?pit > ?empStart)
OPTIONAL { ?employerStmt pq:P582 ?empEnd.
}
FILTER ((bound(?empEnd) && ?pit < ?empEnd) || (!bound(?empEnd) && ?pit > ?empStart) )
OPTIONAL {?employer wdt:P154 ?logoImage.}
}
}AS %results
WHERE {
INCLUDE %results.
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
{
SELECT (COUNT(?vbib) AS ?NoVBIBFix) WHERE {
?vbib wdt:P179 wd:Q108784175.
}
}
}
GROUP BY ?employerLabel ?NoVBIBFix ?logoImage
HAVING (COUNT(?vbib)=?NoVBIBFix)

