query.2737:
SELECT DISTINCT ?l ?lLabel ?coord ?number ?layer
WITH
{
SELECT DISTINCT ?l ?lLabel ?coord (COUNT(DISTINCT ?item) AS ?number)
WHERE
{
?item wdt:P396 [] ; wdt:P19 ?l ; wdt:P27 wd:Q38 . ?l wdt:P625 ?coord .
}
GROUP BY ?l ?lLabel ?coord ?number
} AS %what
WHERE
{
INCLUDE %what
# BIND( IF(?number < 6, "1-5", IF(?number < 21, "6-20", IF(?number < 51, "21-50","51+"))) AS ?layer)
BIND( IF(?number < 31, "1-30", IF(?number < 51, "31-50","51+")) AS ?layer)
SERVICE wikibase:label { bd:serviceParam wikibase:language "it". }
}
GROUP BY ?l ?lLabel ?coord ?number ?layer
ORDER BY DESC(?number)

query.2738:
SELECT DISTINCT ?lighthouse ?lighthouseName ?countryLabel (concat(str(?latitude), ", ", str(?longitude)) AS ?coordinates) ?height ?builtDateLabel ?functionalLabel ?wikipediaUrl
WHERE
{
?lighthouse wdt:P31 wd:Q39715. # find all instances of lighthouse
?country wdt:P30 wd:Q18. # find all instances of country in South America
?lighthouse wdt:P17 ?country. # find lighthouses in South American countries
OPTIONAL {
?lighthouse wdt:P17 ?country.
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}
OPTIONAL {
?lighthouse wdt:P625 ?coordinates.
BIND(xsd:float(STRAFTER(STR(?coordinates), "Point(")) AS ?longitude)
BIND(xsd:float(STRAFTER(STR(?coordinates), " " )) AS ?latitude)
}
OPTIONAL { ?lighthouse wdt:P2048 ?height. }
OPTIONAL { ?lighthouse wdt:P571 ?builtDate. }
OPTIONAL {
?lighthouse p:P1433/ps:P1433 ?wikipediaUrl. # get the English Wikipedia page URL
FILTER (LANG(?wikipediaUrl) = '' || LANG(?wikipediaUrl) = 'en')
}
OPTIONAL {
?lighthouse p:P1317 ?functionalClaim.
?functionalClaim ps:P1317 ?functional.
BIND(IF(?functional, "Operational", "Not operational") AS ?functionalLabel)
}
OPTIONAL {
?lighthouse rdfs:label ?lighthouseName.
FILTER (LANG(?lighthouseName) = 'en')
}
OPTIONAL {
?lighthouse schema:description ?lighthouseName.
FILTER (LANG(?lighthouseName) = 'en')
}
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}
ORDER BY ?lighthouseName

query.2739:
SELECT
?parent ?parentLabel ?parent_born ?parent_genderLabel ?parent_positionLabel ?child ?childLabel ?child_born ?child_genderLabel ?child_positionLabel
