select *
with {
select distinct ?l {
?l dct:language wd:Q188;
wikibase:lexicalCategory wd:Q1084;
wikibase:lemma ?lemma;
wdt:P5238 ?comb1;
wdt:P5238 ?comb2 filter (?comb1 != ?comb2).
?comb1 wikibase:lemma ?combl1.
optional {
?comb1 ontolex:lexicalForm [
wikibase:grammaticalFeature wd:Q107614077;
ontolex:representation ?combf1
]
}
?comb2 wikibase:lemma ?combl2.
bind(concat(replace(coalesce(?combf1, ?combl1), "-$", ""), lcase(replace(?combl2, "^-", ""))) as ?genlemma).
filter(?lemma = ?genlemma).
}
} as %ok
where {
?l dct:language wd:Q188;
wikibase:lexicalCategory wd:Q1084;
wikibase:lemma ?lemma;
wdt:P5238 ?comb.
?comb wikibase:lemma ?combl.
minus { include %ok }
} order by ?lemma ?comb

